#include <iostream>
#include <string>
#include <vector>
#include <sstream>

int countWord(const std::string& word, const std::string& file) {
  std::vector<std::string> words;
  std::sstream& stream(file);
  std::streambuff<std::sstream&>(stream) << word;
  std::get(stream);
  for (const auto& entry : words) {
    if (entry.find(" ") != std::search(entry.begin(), entry.end(), " ")) {
      entry = entry.substr(0, entry.length() - entry.find(" ") - 1);
      entry.insert(entry.end(), word.c_str());
    }
  }
  return words.size();
}

int main() {
  std::string file = "file.txt";
  std::cout << "file.txt 中の " << file << " の出現回数を数えています。" << std::endl;

  std::vector<std::string> words;
  std::sstream stream(file);
  std::streambuff<std::sstream&>(stream) << std::string("word");
  std::get(stream);
  std::sstream& entry;
  while (entry = std::sstream::parse(stream, entry)) {
    words.push_back(entry.c_str());
  }

  std::cout << "word の出現回数: " << words.size() << std::endl;
  return 0;
}
```

